import {React, useState} from "react";
var setName = (event) =>{
    song.name == event.target.elements[0].value;
}
var setYear = (event) =>{
    song.year == event.target.elements[0].value;
}
var setArtist= (event) =>{
    song.artist == event.target.elements[0].value;
}
function EditFormSong(song){
    var {name, setName} = useState("");
    var {year, setYear} = useState("");
    var {artist, setArtist} = useState("");

    var setName = (event) =>{
        song.name == event.target.elements[0].value;
    }
    var setYear = (event) =>{
        song.year == event.target.elements[0].value;
    }
    var setArtist= (event) =>{
        song.artist == event.target.elements[0].value;
    }
    function handleSubmit(event){
        event.preventDefault();
        console.log(song)
        setName(event);
        setName(event);
        setName(event);
        console.log(song)
    }
    return(
        <form onSubmit={handleSubmit}>
            <label>
                Name:
                <input type="text" value={name} onChange={setName}/>
            </label>
            <label>
            Year Released:
                <input type="text" value={year} onChange={setYear}/>
            </label>
            <label>
            Artist:
                <input type="text" value={artist} onChange={setArtist}/>
            </label>
            <button type="submit">Song Save</button>
        </form>
    )
}

export default EditFormSong;
