import {React, useState } from 'react';

function SongForm({onAddSong}){
    var [name, setName] = useState('');
    const [year, setYear] = useState('');
    const [artist, setArtist] = useState('');

    function handleSubmit(event) {
        event.preventDefault();
        onAddSong({name, year,artist});
        setYear('');
        setArtist('');
        setName = (event)=> event.target.value;
        setYear = (event)=> event.target.value;
        setArtist = (event)=> event.target.value;
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Name:
                <input type="text" value={name} onChange={setName()}/>
            </label>
            <label>
                Year:
                <input type="text" value={year} onChange={setYear} />
            </label>
            <label>
                Artist:
                <input type="text" value={artist} onChange={setArtist} />
            </label>
            <button type="submit">Add Song</button>´
        </form>
    );
}

export default SongForm;
