import {React, useState} from "react";

function EditFormSong(song, onSongEdit){
    var {name, setName} = useState("");
    var {year, setYear} = useState("");
    var {artist, setArtist} = useState("");

    function handleSubmit(event){
        event.preventDefault();
        let setName = () => event.target.value;
        let setYear = () => event.target.value;
        let setArtist = () => event.target.value;
        console.log({...song,setName,setYear,setArtist})

    }
    return(
        <form onSubmit={handleSubmit}>
            <label>
                Name:
                <input type="text" value={name} onChange={setName}/>
            </label>
            <label>
            Year Released:
                <input type="text" value={year} onChange={setYear()}/>
            </label>
            <label>
            Artist:
                <input type="text" value={artist} onChange={setArtist()}/>
            </label>
            <button type="submit">Song Save</button>
        </form>
    )
}

export default EditFormSong;
